import "./registerScreen.css";
import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const RegisterScreen = ({ history }) => {

    const navigate = useNavigate();
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmpassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");

    const registerHandler = async (e) => {
        e.preventDefault();
        const config = {
            header: {
                "Content-Type": "application/json",
            },
        };
        if (password !== confirmpassword) {
            setPassword("");
            setConfirmPassword("");
            setTimeout(() => {
                setError("");
        }, 5000);
            return setError("Mật khẩu không khớp");
        }

        try {
            const { data } = await axios.post("/api/auth/register", {
            username,
            email,
            password,
            },
            config
        );
        localStorage.setItem("authToken", data.token);
        navigate("/");
        } catch (error) {
            setError(error.response.data.error);
            setTimeout(() => {
                setError("");
            }, 5000);
        }
    };

  return (
    <div className="register-screen">
      <form onSubmit={registerHandler} className="register-screen__form">
        <h3 className="register-screen__title">Đăng ký</h3>
        {error && <span className="error-message">{error}</span>}

        <fieldset>
                <p>
                  <input type="text" required id="name" placeholder="Enter username" value={username} onChange={(e) => setUsername(e.target.value)} />
                </p>
                <p>
                  <input type="email" required id="email" placeholder="Email address" value={email} onChange={(e) => setEmail(e.target.value)} />
                </p>
                <p>
                    <a href="/">Quên mật khẩu?</a>
                    <a href="/register">Bạn chưa có tài khoản? Đăng ký ngay</a>
                </p>
                <p>
                    <input type="submit" value="Đăng nhập"/>
                </p>
            </fieldset>

        <div className="form-group">
          <label htmlFor="name">Username:</label>
          
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            required
            id="password"
            autoComplete="true"
            placeholder="Enter password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label htmlFor="confirmpassword">Confirm Password:</label>
          <input
            type="password"
            required
            id="confirmpassword"
            autoComplete="true"
            placeholder="Confirm password"
            value={confirmpassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Register
        </button>

        <span className="register-screen__subtext">
          Already have an account? <Link to="/login">Login</Link>
        </span>
      </form>
    </div>
  );
};

export default RegisterScreen;
