import './product.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { productSlider } from '../../dummyData';
import Slider from 'react-slick';
import { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams, useNavigate } from 'react-router-dom'; 

//import Actions
import { getProductDetails } from '../../redux/actions/productActions';


const Product = () =>{

  const [qty, setQty] = useState();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const productDetails = useSelector((state) => state.getProductDetails);
  const {loading, error, product} = productDetails;
  const { id } = useParams();

  useEffect(() =>{
    if(product && id !== product._id){
      dispatch(getProductDetails(id));
    }
  },[dispatch, id, product]);

  const addToCartHandle = () =>{
    navigate('/cart');
  }

  return (
    <div className='product'>
        <div className="productscreen__left">
          <div className="left__image">
            <Slider {...productSlider}>
              <div className="">
                  <img className='bannerImg' src="https://scontent.fhan14-2.fna.fbcdn.net/v/t1.6435-9/122145096_402515241151117_560129999522686289_n.jpg?_nc_cat=109&ccb=1-5&_nc_sid=8bfeb9&_nc_ohc=-kWwRgEWe1EAX8YUgB4&tn=kINUsOAA-IoCOhgK&_nc_ht=scontent.fhan14-2.fna&oh=00_AT_scaGc8DS0K2nzC0rcfsQwtfjyBH3uXjZ5rQNfmKhWEg&oe=62494343" alt="" />
              </div>
              <div className="">
                  <img className='bannerImg' src="https://scontent.fhan14-1.fna.fbcdn.net/v/t1.6435-9/104393798_760218851455180_4279034890704564360_n.jpg?_nc_cat=110&ccb=1-5&_nc_sid=8bfeb9&_nc_ohc=vnKFtqg440cAX8ulUkL&_nc_ht=scontent.fhan14-1.fna&oh=00_AT-TrNOuNXmShwxcMv_-_BnI-vnBrZLz5PIt4qQVxkBQUw&oe=624745D1" alt="" />
              </div>
              <div className="">
                  <img className='bannerImg' src="https://scontent.fhan14-2.fna.fbcdn.net/v/t1.6435-9/122145096_402515241151117_560129999522686289_n.jpg?_nc_cat=109&ccb=1-5&_nc_sid=8bfeb9&_nc_ohc=-kWwRgEWe1EAX8YUgB4&tn=kINUsOAA-IoCOhgK&_nc_ht=scontent.fhan14-2.fna&oh=00_AT_scaGc8DS0K2nzC0rcfsQwtfjyBH3uXjZ5rQNfmKhWEg&oe=62494343" alt="" />
              </div>
              <div className="">
                  <img className='bannerImg' src="https://scontent.fhan14-2.fna.fbcdn.net/v/t1.6435-9/122145096_402515241151117_560129999522686289_n.jpg?_nc_cat=109&ccb=1-5&_nc_sid=8bfeb9&_nc_ohc=-kWwRgEWe1EAX8YUgB4&tn=kINUsOAA-IoCOhgK&_nc_ht=scontent.fhan14-2.fna&oh=00_AT_scaGc8DS0K2nzC0rcfsQwtfjyBH3uXjZ5rQNfmKhWEg&oe=62494343" alt="" />
              </div>
            </Slider>
          </div>
          
        </div>
        <div className="productscreen__right">
          {loading ? (
            <h2>Loading...</h2>
          ) : error ? (
            <h2>{error}</h2>
          ) : (
            <>
              <div className="left__info">
                <p className="left__name">Nutri Canxi Nano</p>
                <p>Giá: 13.000 đ</p>
                <p>Mô tả: Thực phẩm bổ sung Pro Multi plusssz giúp bổ sung Vitamin C, hỗ trợ tăng cường sức đề kháng và hỗ trợ giảm các triệu chứng cảm cúm, đau đầu, hắt hơi sổ mũi, đau mỏi cơ thể.</p>
              </div>
              <div className="right__info">
                <p>
                  Giá:
                  <span>13.000 đ</span>
                </p>
                <p>
                  Đơn vị:
                  <span>
                    Tuýp
                  </span>
                </p>
                <p>
                  Số lượng còn:
                  <span>100000</span>
                </p>
                <p>
                  <button type="button" onClick={addToCartHandle}>
                    Thêm vào giỏ hàng
                  </button>
                </p>
              </div>
            </>
          )}
        </div>
    </div>
  )
}

export default Product
