import './cartItem.css';
import { Link } from 'react-router-dom';
import DeleteOutlineIcon from '@material-ui/icons/DeleteOutline';

const CartItem = ({item, amountChangeHandle, removeFromCartHandle}) =>{
  return (
    <div className='cartItem'>
        <div className="cartitem__image">
            <img src={item.img} alt={item.title} />
        </div>

        <Link to={`/product/${item._id}`} className="cartitem__name">
            <p>{item.title}</p>
        </Link>

        <p className="cartitem__unit">Tu√Ωp</p>
        <p className="cartitem__price">13.000d</p>

        <p className="cartitem__qty">60</p>

        <button className="cartitem__deleteBtn" >
            <DeleteOutlineIcon />
        </button>
    </div>
  )
}

export default CartItem
