import './cartScreen.css';
import { useDispatch, useSelector } from 'react-redux';

//import Componrnts
import CartItem from '../../components/cartItem/CartItem';

//import Actions
import { addToCart, removeFromCart } from '../../redux/actions/cartActions';
import { Link } from 'react-router-dom';

const CartScreen = () =>{

  const dispatch = useDispatch();
  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  const amountChangeHandle = (id, amount) =>{
    dispatch(addToCart(id, amount));
  }

  const removeFromCartHandle = (id) =>{
    dispatch(removeFromCart(id));
  }

  const getCartCount = () =>{
    return cartItems
    .reduce((amount, item) => Number(item.amount) + amount, 0);
  }

  const getCartSubTotal = () =>{
    return cartItems
    .reduce((price, item) => price + item.price * item.amount, 0)
    .toFixed(2);
  }

  return (
    <div className='cartScreen'>
      <div className="cartscreen__left">
      <h2>GIỎ HÀNG</h2>
      {cartItems.length == 0 ? (
        <div>Giỏ hàng đang trống <Link to='/'>Quay trở lại</Link></div>
      ) : cartItems.map(item =>(
        <CartItem
        item={item} amountChangeHandle={amountChangeHandle} removeFromCartHandle={removeFromCartHandle}
        />
      ))}
      </div>
      <div className="cartscreen__right">
        <div className="cartscreen__info">
          <p>Tổng ({getCartCount()}) sản phẩm</p>
          <p>{getCartSubTotal()} đ</p>
        </div>
        <div>
          <button>Tiến hành đặt hàng</button>
        </div>
      </div>
    </div>
  )
}

export default CartScreen
