import './product.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from 'react-slick';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { useDispatch } from 'react-redux';

//import Slider
import { productSlider } from '../../dummyData';

//import Actions
import { addToCart } from '../../redux/actions/cartActions';

//import Validator
import { useForm } from 'react-hook-form';

const Product = ({productId, title, img, price, unit, qty, description}) =>{

  const dispatch = useDispatch();
  const [amount, setAmount] = useState();
  const navigate = useNavigate();

  const addToCartHandle = () =>{
    dispatch(addToCart(productId, amount));
    navigate('/cart');
  }

  const {
    register,
    handleSubmit,
    formState: { errors }
  } = useForm();

  const onSubmit = () =>{

  }

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
    <div className='product'>
      <div className="productscreen__left">
        <div className="left__image">
          <Slider {...productSlider}>
            <div className="">
                <img className='bannerImg' src={img} alt={title} />
            </div>
            <div className="">
                <img className='bannerImg' src={img} alt={title} />
            </div>
          </Slider>
        </div>
      </div>
      <div className="productscreen__right">
        <div className="left__info">
          <p className="left__name">{title}</p>
          <p>Giá: {price} đ</p>
          <p>Mô tả: {description}</p>
        </div>
        <div className="right__info">
          <p>
            Số lượng:
            <span>
            
              <input 
              {...register("qty", {
                required: true,
                valueAsNumber: true
              })} 
            type="number" value={amount} onChange={(e) => setAmount(e.target.value)}/>
            {errors?.qty?.type === "required" && <p>Vui lòng nhập số lượng sản phẩm</p>}
            {errors?.qty?.type === "valueAsNumber" && <p>Nhập đúng định dạng số</p>}
            
            </span>
          </p>
          <p>
            Đơn vị:
            <span>
              {unit}
            </span>
          </p>
          <p>
            Số lượng còn:
            <span>{qty}</span>
          </p>
          <p>
            <button type="submit" onClick={addToCartHandle}>
              Thêm vào giỏ hàng
            </button>
          </p>
        </div>
      </div>
    </div>
    </form>
  )
}

export default Product
