import './product.css';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import Slider from 'react-slick';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import { useDispatch } from 'react-redux';

//import Slider
import { productSlider } from '../../dummyData';

//import Actions
import { addToCart } from '../../redux/actions/cartActions';

//import Validator
import Validator from '../../../../utils/validators';

const Product = ({productId, title, img, price, unit, qty, description}) =>{

  this.state = {
    qty: '',
  };

  const requiredWith = (value, field, state) => (!state[field] && !value) || !!value;

  const dispatch = useDispatch();
  const [amount, setAmount] = useState();
  const navigate = useNavigate();

  const addToCartHandle = () =>{
    dispatch(addToCart(productId, amount));
    navigate('/cart');
  }

  return (
    <div className='product'>
      <div className="productscreen__left">
        <div className="left__image">
          <Slider {...productSlider}>
            <div className="">
                <img className='bannerImg' src={img} alt={title} />
            </div>
            <div className="">
                <img className='bannerImg' src={img} alt={title} />
            </div>
          </Slider>
        </div>
      </div>
      <div className="productscreen__right">
        <div className="left__info">
          <p className="left__name">{title}</p>
          <p>Giá: {price} đ</p>
          <p>Mô tả: {description}</p>
        </div>
        <div className="right__info">
          <p>
            Số lượng:
            <span>
              <input type="text" value={amount} onChange={(e) => setAmount(e.target.value)}/>
              
            </span>
          </p>
          <p>
            Đơn vị:
            <span>
              {unit}
            </span>
          </p>
          <p>
            Số lượng còn:
            <span>{qty}</span>
          </p>
          <p>
            <button type="button" onClick={addToCartHandle}>
              Thêm vào giỏ hàng
            </button>
          </p>
        </div>
      </div>
    </div>
  )
}

export default Product
